import React, { useState, useEffect, useContext } from "react";
import { useCookies } from "react-cookie";
import axios from "axios";

import UserContext from "./Utils/Contexts/UserContext";

import Sidebar from "./Components/Sidebar";
import NoteList from "./Components/NoteList";
import Account from "./Components/Account";

import './App.css';

function App() {

  const { username, id } = useContext();
  const [ cookies, setCookies ] = useCookies();
  const [ user, setUser ] = useState({});

  const getUserInfo = async () => {
    try {
      const { data } = await axios.get("/account/user");
      setUser(data);
    } catch (error) {
      console.error(error);
    }
  }

  // get user on page load if a cookie exists for micronote
  useEffect(() => {
    if (cookies.micronote && !user.id) {
      getUserInfo();
    } else return;
  }, [])

  // pass down functions for the account management
  // REGISTER / LOGIN / LOGOUT sets user state and cookies

  // DEBUG - making sure the user is being set
  useEffect(() => {
    if (user.id) console.log(user);
  }, [user])

  return (

    <UserContext.Provider value={user.username}>

      <div className="App">

        {/* account management */}
        <Account />

        {/* sidebar */}
        <div id="sidebar">
          <Sidebar />
        </div>

        {/* main content */}
        <div id="main">
          <NoteList />
        </div>

      </div>

    </UserContext.Provider>
  );
}

export default App;
