import axios from "axios";
import { useState } from "react";
import * as localforage from "localforage";

import "./Account.css";

const Account = (props) => {

    const [ registerUsername, setRegisterUsername ] = useState("");
    const [ registerPassword, setRegisterPassword ] = useState("");
    const [ loginUsername, setLoginUsername ] = useState("");
    const [ loginPassword, setLoginPassword ] = useState("");

    const [ user, setUser ] = useState()

    const register = async (event) => {
        event.preventDefault();

        let localNotes = await localforage.getItem("notes");

        axios.post("/account/register", {
            username: registerUsername,
            password: registerPassword,
            localNotes: localNotes
        })
            .then(res => console.log(res))
            .catch(err => console.error(err));
    };

    const login = async (event) => {
        event.preventDefault();
        const noteList = await localforage.getItem("notes");

        axios.post("/account/login", {
            username: loginUsername,
            password: loginPassword,
            localNotes: noteList
        })
            .then(res => {
                console.log(res.data);
                setUser(res.data);
            }).catch(err => console.error(err))
        
    };

    const getUser = async () => {
        axios.get("/account/user")
            .then(res => console.log(res.data))
    }

    const getNotes = async () => {
        axios.get("/notes")
            .then(res => console.log(res.data.notes));
    }

    const syncNotes = async() => {
        const noteList = await localforage.getItem("notes");

        axios.post("/notes", { 
            list: noteList
        })
            .then(res => console.log(res.data))
            .catch(err => console.error(err));
    }

    const logout = async() => {
        axios.post("/account/logout")
            .then(res => console.log(res.data))
            .catch(err => console.error(err));
    }

    
    return (
        <div id="account-details">
            {/* This is all going to need to change depending on if we have a micronote cookie */}

            {<h3>username go here</h3>}

            {/* Don't show this if there's a cookie */}

            <form id="register">
                <h4>Register Account</h4>
                <div>
                    <label htmlFor="username">Username</label>
                    <input type="text" 
                        onChange={e => setRegisterUsername(e.target.value)}
                        id="username" 
                        name="username"  
                        placeholder="username"
                        required />
                </div>
                <div>
                    <label htmlFor="password">Password</label>
                    <input type="password"
                        onChange={e => setRegisterPassword(e.target.value)}
                        id="password"
                        name="username"
                        placeholder="password"
                        required />
                </div>
                <button onClick={register} type="submit">Register</button>
            </form>

            <form id="login">
                <h4>Log In</h4>
                <div>
                    <label htmlFor="username">Username</label>
                    <input type="text" 
                        onChange={e => setLoginUsername(e.target.value)}
                        id="username" 
                        name="username" 
                        placeholder="username"
                        required />
                </div>
                <div>
                    <label htmlFor="password">Password</label>
                    <input type="password"
                        onChange={e => setLoginPassword(e.target.value)}
                        id="password"
                        name="username"
                        placeholder="password"
                        required />
                </div>
                <button onClick={login} type="submit">Login</button>
            </form>

            {/* Some account management stuff would go here, if we had a cookie */}

            <div id="debug">
                <button onClick={getUser}>User Info</button>
                <button onClick={getNotes}>Show Notes</button>
                <button onClick={syncNotes}>Send Notes</button>
                <button onClick={logout}>Log Out</button>
            </div>
        
        </div>
    )
}

export default Account;
