import React, { useState, useEffect } from "react";
import { useCookies } from "react-cookie";
import axios from "axios";

import Sidebar from "./Components/Sidebar";
import NoteList from "./Components/NoteList";
import Account from "./Components/Account";

import './App.css';

function App() {

  const [ cookies, setCookies ] = useCookies();
  const [ user, setUser ] = useState({});

  const getUserInfo = async () => {
    try {
      const { data } = await axios.get("/account/user");
      setUser(data);
    } catch (error) {
      console.error(error);
    }
  }

  // get user on page load if a cookie exists for micronote
  useEffect(() => {
    if (cookies.micronote && !user.id) {
      getUserInfo();
    } else return;
  }, [])

  // pass down functions for the account management
  // REGISTER / LOGIN / LOGOUT sets user state and cookies

  const updateUserState = async (user) => {
    await setUser(user)
  }

  // DEBUG - making sure the user is being set
  // useEffect(() => {
  //   if (user.id) console.log(user);
  // }, [user])

  return (

      <div className="App">

        {/* account management */}
        <Account id={user.id} username={user.username} updateUserState={updateUserState} />

        {/* sidebar */}
        <div id="sidebar">
          <Sidebar username={user.username} />
        </div>

        {/* main content */}
        <div id="main">
          <NoteList userID={user.id} />
        </div>

      </div>
  );
}

export default App;
