import React, { useState, useEffect } from "react";
import { useCookies } from "react-cookie";
import axios from "axios";
import localforage from "localforage";

import Sidebar from "./Components/Sidebar";
import NoteList from "./Components/NoteList";
import Account from "./Components/Account";

import './App.css';

function App() {

  const [ cookies, setCookies ] = useCookies();
  const [ user, setUser ] = useState({});
  const [ noteList, setNoteList] = useState([]);

  const getUserInfo = async () => {
    try {
      let userData, userNotes;

      if (cookies.micronote) {
        console.log("Getting internet data")
        userData = await axios.get("/account/user");
        userNotes = await
        console.log(userData.data);
      } else {
        console.log("getting local data");
        userData = await localforage.getItem("notes");
        console.log(userData);
      }

      setUser(userData);
    } catch (error) {
      console.error(error);
    }
  }

  // get user on page load if a cookie exists for micronote
  useEffect(() => {
    getUserInfo();
  }, [cookies])

  // pass down functions for the account management
  // REGISTER / LOGIN / LOGOUT sets user state and cookies

  const updateUserState = async (user) => {
    await setUser(user);
  }

  // DEBUG - making sure the user is being set
  // useEffect(() => {
  //   if (user.id) console.log(user);
  // }, [user])

  return (

      <div className="App">

        {/* account management */}
        <Account id={user.id} username={user.username} updateUserState={updateUserState} />

        {/* sidebar */}
        <div id="sidebar">
          <Sidebar username={user.username} />
        </div>

        {/* main content */}
        <div id="main">
          <NoteList userID={user.id} />
        </div>

      </div>
  );
}

export default App;
