import React, { useState, useEffect } from "react";
import { useCookies } from "react-cookie";
import axios from "axios";
import localforage from "localforage";

import Sidebar from "./Components/Sidebar";
import NoteList from "./Components/NoteList";
import Account from "./Components/Account";

import './App.css';
import _ from "lodash";

function App() {

  const [ cookies, setCookies ] = useCookies();
  const [ user, setUser ] = useState({});
  const [ notes, setNotes ] = useState([]);

  // get user on page load if a cookie exists for micronote
  useEffect(() => {
    const getUserInfo = async () => {
      try {
        if (cookies.micronote) {

          console.log("Getting internet data");
          const { data } = await axios.get("/account/user");
          setUser({ id: data.id, username: data.username });
          setNotes(data.notes);

        } else {

          console.log("getting local data");
          const localNotes = localforage.getItem("notes");

          if (localNotes) {
            setNotes(localNotes)
          } else {
            localforage.setItem("notes", []);
          };

        }
      } catch (error) {
        console.error(error);
      };
    };

    getUserInfo();

  }, [cookies])

  // pass down functions for the account management
  // REGISTER / LOGIN / LOGOUT sets user state and cookies

  const updateUserState = async (user) => {
    await setUser(user);
  }

  // DEBUG - making sure the user is being set
  useEffect(() => {
    if (user.id) console.log(user);
    if (notes.length > 0) console.log(notes);
  }, [user, notes])

  return (

      <div className="App">

        {/* account management */}
        <Account id={user.id} username={user.username} updateUserState={updateUserState} />

        {/* sidebar */}
        <div id="sidebar">
          <Sidebar username={user.username} />
        </div>

        {/* main content */}
        <div id="main">
          <NoteList notes={notes} />
        </div>

      </div>
  );
}

export default App;
