import axios from "axios";
import { useState } from "react";
import * as localforage from "localforage";

import "./Account.css";

const Account = () => {

    const [ registerUsername, setRegisterUsername ] = useState("");
    const [ registerPassword, setRegisterPassword ] = useState("");
    const [ loginUsername, setLoginUsername ] = useState("");
    const [ loginPassword, setLoginPassword ] = useState("");

    const [ user, setUser ] = useState()

    const register = async (event) => {
        event.preventDefault();

        let noteList = await localforage.getItem("notes");

        axios.post("/account/register", {
            username: registerUsername,
            password: registerPassword,
            localNotes: noteList
        })
            .then(res => console.log(res))
            .catch(err => console.error(err));
    };

    const login = async (event) => {
        event.preventDefault();

        axios.post("/account/login", {
            username: loginUsername,
            password: loginPassword
        })
            .then(res => {
                console.log(res.data);
                setUser(res.data);
            }).catch(err => console.error(err))
        
    };

    const getUser = async () => {
        axios.get("/account/user")
            .then(res => console.log(res.data))
    }

    const getNotes = async () => {
        axios.get("/notes")
            .then(res => console.log(res.data.notes));
    }

    const syncNotes = async() => {
        const noteList = await localforage.getItem("notes");

        axios.post("/notes", { 
            list: noteList
        })
            .then(res => console.log(res.data))
            .catch(err => console.error(err))
    }

    return (
        <div id="account-details">
                {user ? <p>{user.username}</p> : <p>guest</p>}

                <form id="register">
                    <h4>Register Account</h4>
                    <div>
                        <label htmlFor="username">Username</label>
                        <input type="text" 
                            onChange={e => setRegisterUsername(e.target.value)}
                            id="username" 
                            name="username"  
                            placeholder="username"
                            required />
                    </div>
                    <div>
                        <label htmlFor="password">Password</label>
                        <input type="password"
                            onChange={e => setRegisterPassword(e.target.value)}
                            id="password"
                            name="username"
                            placeholder="password"
                            required />
                    </div>
                    <button onClick={register} type="submit">Register</button>
                </form>

                <form id="login">
                    <h4>Log In</h4>
                    <div>
                        <label htmlFor="username">Username</label>
                        <input type="text" 
                            onChange={e => setLoginUsername(e.target.value)}
                            id="username" 
                            name="username" 
                            placeholder="username"
                            required />
                    </div>
                    <div>
                        <label htmlFor="password">Password</label>
                        <input type="password"
                            onChange={e => setLoginPassword(e.target.value)}
                            id="password"
                            name="username"
                            placeholder="password"
                            required />
                    </div>
                    <button onClick={login} type="submit">Login</button>
                </form>

                <div id="debug">
                    <button onClick={getUser}>User Info</button>
                    <button onClick={getNotes}>Show Notes</button>
                    <button onClick={syncNotes}>Send Notes</button>
                </div>
            
            </div>
    )
}

export default Account;
