import axios from "axios";
import { useState } from "react";
import * as localforage from "localforage";

import { FontAwesomeIcon as FA} from '@fortawesome/react-fontawesome'
import { faAngleLeft } from '@fortawesome/free-solid-svg-icons'

import "./Account.css";

const Account = ({ toggle, show, id, username, updateUserState, updateNoteList }) => {

    const [ registerUsername, setRegisterUsername ] = useState("");
    const [ registerPassword, setRegisterPassword ] = useState("");
    const [ loginUsername, setLoginUsername ] = useState("");
    const [ loginPassword, setLoginPassword ] = useState("");

    let accountClasses = ["account"];

    if (show) {
        accountClasses.push("show");
    }

    const register = async (event) => {
        event.preventDefault();

        let localNotes = await localforage.getItem("notes");

        axios.post("/account/register", {
            username: registerUsername,
            password: registerPassword,
            localNotes: localNotes
        })
            .then(res => {
                console.log(res.data);
                updateUserState({
                    id: res.data._id,
                    username: res.data.username
                })
            })
            .catch(err => console.error(err));
    };

    const login = async (event) => {
        event.preventDefault();
        const noteList = await localforage.getItem("notes");

        await axios.post("/account/login", {
            username: loginUsername,
            password: loginPassword,
            localNotes: noteList
        })
            .then(res => {
                updateUserState(res.data);
                updateNoteList(res.data.notes);
            })
            .catch(err => console.error(err))
    };

    const logout = async() => {
        axios.post("/account/logout")
            .then(updateUserState({}))
            .catch(err => console.error(err));
    }

    
    return (
        <div className={accountClasses.join(" ")}>
            {/* This is all going to need to change depending on if we have a micronote cookie */}

            <button className="close" onClick={toggle}><FA icon={faAngleLeft} /></button>

            {username && <h3>{username}</h3>}

            {/* Don't show this if there's a cookie */}

            {!id && 
            <div className="login-register">

                <p>Register an account to sync notes</p>

                <form id="register" onSubmit={register}>
                    <div>
                        <label htmlFor="username">Username</label>
                        <input type="text" 
                            onChange={e => setRegisterUsername(e.target.value)}
                            id="username" 
                            name="username"  
                            placeholder="username"
                            required />
                    </div>
                    <div>
                        <label htmlFor="password">Password</label>
                        <input type="password"
                            onChange={e => setRegisterPassword(e.target.value)}
                            id="password"
                            name="username"
                            placeholder="password"
                            required />
                    </div>
                    <button onClick={register} type="submit">Register</button>
                </form>

                <form id="login" onSubmit={login}>
                    <div>
                        <label htmlFor="username">Username</label>
                        <input type="text" 
                            onChange={e => setLoginUsername(e.target.value)}
                            id="username"
                            name="username" 
                            placeholder="username"
                            required />
                    </div>
                    <div>
                        <label htmlFor="password">Password</label>
                        <input type="password"
                            onChange={e => setLoginPassword(e.target.value)}
                            id="password"
                            name="username"
                            placeholder="password"
                            required />
                    </div>
                    <button onClick={login} type="submit">Login</button>
                </form>
            </div>
            }

            {/* Some account management stuff would go here, if we had a cookie */}

            <div className="actions">
                { username && <button onClick={logout}>Log Out</button> }
            </div>
        
        </div>
    )
}

export default Account;
