    } catch (error) {
      console.error(error);
    }

  }

  useEffect(() => {
    if (cookies.micronote && !user.id) {
      getUserInfo();
    }
  }, [getUserInfo])

  return (

    <UserContext.Provider value={user.username}>

      <div className="App">

        {/* account management */}
        <Account />

        {/* sidebar */}
        <div id="sidebar">
          <Sidebar />
        </div>

        {/* main content */}
        <div id="main">
          <NoteList />
        </div>

      </div>

    </UserContext.Provider>
  );
}

export default App;
